import {
  Routes,
  Route,
  useNavigationType,
  useLocation,
} from "react-router-dom";
import HomePage from "./pages/home-page-of-takk"
import Login from './user/login/Login';
import Signup from './user/signup/Signup';
import QuestionsAfterSignUp from './user/signup/QuestionsAfterSignUp';
import MacBookPro161 from "./pages/mac-book-pro161";
import { useEffect } from "react";
import ListCommunityPage from "./pages/ListCommunityPage";
import ViewCommunityPage from "./pages/ViewCommunityPage";
import InitiativeListPage from "./pages/InitiativeListPage";
import InitiativeCreateEditPage from "./pages/InitiativeCreateEditPage";
import PersonalDashBoard from "./pages/PersonalDashBoard";
import DataProvider from "./context/DataContext";

import NavBar from "./components/HomePageComponents/nav-bar";

import { Link } from "react-router-dom";

function App() {
  const action = useNavigationType();
  const location = useLocation();
  const pathname = location.pathname;

  useEffect(() => {
    if (action !== "POP") {
      window.scrollTo(0, 0);
    }
  }, [action, pathname]);

  useEffect(() => {
    let title = "";
    let metaDescription = "";

    switch (pathname) {
      case "/":
        title = "";
        metaDescription = "";
        break;
    }

    if (title) {
      document.title = title;
    }

    if (metaDescription) {
      const metaDescriptionTag = document.querySelector(
        'head > meta[name="description"]'
      );
      if (metaDescriptionTag) {
        metaDescriptionTag.content = metaDescription;
      }
    }
  }, [pathname]);

  return (
    <>
    <NavBar />

    <DataProvider>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/loginpage" element={<Login />} />
        <Route path="/signup"    element={<Signup />} />
        <Route path="/questionsaftersignup" element={<QuestionsAfterSignUp />} />
        <Route path="/CreateEditCommunity" element={<MacBookPro161 />} />
        <Route path="/CommunityList" element={<ListCommunityPage />} />
        <Route path="/CommunityPage" element={<ViewCommunityPage />} />

        <Route path="/InitiativeList" element={<InitiativeListPage />} />        
        <Route path="/InitiativeCreateEdit" element={<InitiativeCreateEditPage />} />

        <Route path="/PersonalDashBoard" element={<PersonalDashBoard />} />

      </Routes>
    </DataProvider>
    </>
  );
}
export default App;
